// generates pending, fulfilled and rejected action types
export const fetchUsers = createAsyncThunk('user/fetchUsers', () => {
    return axios
        .get("/users")
        .then((response) => response.data)
})

const userSlice = createSlice({
    name: 'user',
    initialState,
